import base64
import json
from urllib.parse import unquote

# body when command was sent
payload_1 = ''

# body when button was clicked
payload_2 = ''

data = base64.b64decode(payload_2)
body = unquote(data)


if body.startswith('token'):
    items_list = list()
    for item in body.split('&'):
        item = tuple(item.split('='))
        items_list.append(item)
    body = dict(items_list)

elif body.startswith('payload'):
    body = json.loads(body.split('=')[1])

print(body)
